<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');if (!$posts){	echo '<h1>'.$this->lang->line('noPosts').'</h1>';}echo '<div class="page_list"><ul class="posts">';$counter = 1;foreach ($firstPost as $row){$counter++;if($row['Title'])    { ?>	<li <?php if($this->ion_auth->logged_in() && $this->ion_auth->is_admin() && $row['reported'] == '1'){ echo 'class="reported"'; $counter = 0;}elseif ($counter ==2) { echo ' class="alt"'; $counter = 0; } ?>>        <div class="avatar">            <?php                 echo '<img src="'.$this->users_m->gravatar($row['email'], "x", "45").'" title="'.$row['username'].''.$this->lang->line('avatarTitle').'" height="45px" width="45px"/>';            ?>        </div>    <?php        if ($posts)         {            if ($category)             {                echo '<h2>'.$topicName.'</h2>';            }        }    ?>	<?php    $bookmark_check = $this->topics_m->check_bookmark($this->uri->segment(4), $this->session->userdata('user_id'));	echo '<p class="posted">'.anchor('forums/profile/'.$row['username'].'', $row['username'], 'title="'.$row['username'].''.$this->lang->line('profileTitle').'"').' <cite>'.$this->timeword->convert($row['PostTime'], time()).' '.$this->lang->line('postsAgo').'</cite></p>';	echo '<div class="buttons">';    if($this->ion_auth->logged_in())    {        if($bookmark_check == false)        {            echo '<div class="smallButton"><div class="pageButton"><div class="content"><img src="'.base_url().'assets/images/like_icon.png" width="15" height="15"/><a href="'.site_url().'/forums/remove_bookmark/'.$this->uri->segment(4).'/" title="'.$this->lang->line('postsRemoveBookmark').'">'.$this->lang->line('postsButtonRemoveBookmark').'</a></div></div></div>';                }        else        {            echo '<div class="smallButton"><div class="pageButton"><div class="content"><img src="'.base_url().'assets/images/like_icon.png" width="15" height="15"/><a href="'.site_url().'/forums/bookmark_topic/'.$this->uri->segment(4).'/" title="'.$this->lang->line('postsAddBookmark').'">'.$this->lang->line('postsButtonAddBookmark').'</a></div></div></div>';            }        echo '<div class="smallButton"><div class="pageButton"><div class="content"><img src="'.base_url().'assets/images/comment_icon.png" width="15" height="14" /><a href="#comments_box" title="'.$this->lang->line('postsQuickReply').'">'.$this->lang->line('postsButtonQuickReply').'</a></div></div></div>';    }    echo '</div>';    echo '<div class="comment">'.nl2br($row['Body']).'</div>';	echo '</li>';	echo '<div id="pagination">';	echo $links;	echo '</div>';}foreach($posts as $row){        if(!$row['Title'])    {?>	<li <?php if($this->ion_auth->logged_in() && $this->ion_auth->is_admin() && $row['reported'] == '1'){ echo 'class="reported"'; $counter = 0;}elseif ($counter ==2) { echo ' class="alt"'; $counter = 0; } ?>>	<div class="avatar">    <?php	   echo '<img src="'.$this->users_m->gravatar($row['email'], "x", "45").'" title="'.$row['username'].''.$this->lang->line('avatarTitle').'" height="45px" width="45px"/>';	?>    </div>	<?php	echo '<h2>'.anchor('forums/profile/'.$row['username'].'', $row['username'], 'title="'.$row['username'].''.$this->lang->line('profileTitle').'"').'</h2>';    echo '<p class="posted"> <cite>'.$this->timeword->convert($row['PostTime'], time()).' '.$this->lang->line('postsAgo').'</cite></p>';    echo '<div class="post_icons">';    if($this->ion_auth->logged_in())    {    	if($this->ion_auth->is_admin())    	{        	echo ''.anchor('forums/edit_post/'.$row['CommentID'].'', '<img src="'.base_url().'assets/images/edit_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsEditTitle').'"/>').'';        	echo ''.anchor('forums/delete_post/'.$row['CommentID'].'', '<img src="'.base_url().'/assets/images/delete_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsDeleteTitle').'" />').'';    	    	}        if($this->ion_auth->is_group('members'))        {            if($row['username'] == $this->session->userdata('username'))            {                if($editOwnPosts == '1')                {                    echo ''.anchor('forums/edit_post/'.$row['CommentID'].'', '<img src="'.base_url().'assets/images/edit_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsEditTitle').'"/>').'';                    }                if($deleteOwnPosts == '1')                {                    echo ''.anchor('forums/delete_post/'.$row['CommentID'].'', '<img src="'.base_url().'/assets/images/delete_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsDeleteTitle').'" />').'';                    }            }        }        if($this->ion_auth->is_group('moderators'))        {            if($modsEditPosts == '1')            {                echo ''.anchor('forums/edit_post/'.$row['CommentID'].'', '<img src="'.base_url().'/assets/images/edit_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsEditTitle').'"/>').'';                }            if($modsDeletePosts == '1')            {                echo ''.anchor('forums/delete_post/'.$row['CommentID'].'', '<img src="'.base_url().'/assets/images/delete_icon.png" height="15px" width="15px" title="'.$this->lang->line('postsDeleteTitle').'" />').'';                }        }    }    echo '</div>';	echo '<div class="comment">'.nl2br($row['Body']).'</div>';	echo '<div class="clear"></div>';	echo '</li>';}}}	echo '</ul></div>';	echo '<div id="pagination">';	echo $links;	echo '</div>';	if($row['Closed'] == '1')	{		echo '<h3>'.$this->lang->line('postsDiscussionClosed').'</h3>';	}	else	{		if (!$this->ion_auth->logged_in())		{			echo '<h3>'.$this->lang->line('errorNoQuickReply').'</h3>';		}		if ($this->ion_auth->logged_in())		{			$this->load->view('/sections/postReply');		}	}	           #End of file /views/details.php